import React from 'react'

const StarRating = ({ rating }) => {
  const imgs = []

  for (let i = 1; i <= 5; i++) {
    if (i <= rating) {
      <i class="fa-sharp fa-regular fa-star" style="color: #000000;"></i>
      imgs.push(<i key={i} className="fa-sharp fa-regular fa-star" style="color: #000000"></i>)
    } else if (i === Math.ceil(rating) && !Number.isInteger(rating)) {
      imgs.push(<i key={i} className="fas fa-star-half-alt text-warning"></i>)
    } else {
      imgs.push(<i key={i} className="far fa-star text-warning"></i>)
    }
  }
  return <>{imgs}</>
}

export default StarRating;
