import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import RestaurantAPI from '../apis/RestaurantAPI'
import { RestContext } from '../context/RestContext'
import StarRating from '../components/StarRating'
import Reviews from '../components/Reviews'
import AddReview from '../components/AddReview'

  const Details = () => {
  const {id} = useParams()
  const {restReview, setRestReview} = useContext(RestContext)
  const {roundAVG} = 0

  useEffect(() => {
    const fetch = async () => {
      try {
        const response = await RestaurantAPI.get(`/${id}`)
        console.log(response)
        setRestReview(response.data.data)
        roundAVG = response.data.data.score.avg
        console.log(roundAVG)
      } catch (error) {
        console.log(error)
      }
    }

    fetch()
  }, [])

  return (    
    <div className='container'> 
    {restReview && (
        <>
          <h1 className='font-weight-light display-3 text-center mb-4 mt-2'>{restReview.restaurant.name}</h1>
          <div className='row  text-center'>
            <span><StarRating rating = {restReview.score}/></span>
            <h4>{restReview.score}</h4>
          </div>
            <div className='mt-3'>
              <Reviews reviews = {restReview.reviews}/>
            </div>   
            <AddReview/>
        </>
          
        )}        
    </div>
  )
}

export default Details;
