import React, {useState, createContext} from "react"
export const RestContext = createContext()
export const RestContextProvider = props =>{

    const [restaurants, setRestaurants] = useState ([])
         //restaurants retrieved from backend, setting/creating new restaurants
    const [restReview, setRestReview] = useState ([])
        //restaurant review from backend retrieved from first, create new reviews with second
    
    return(
        //passing the value of restaurants to each component
        <RestContext.Provider value = {{restaurants, setRestaurant, restReview, setRestReview}}> 
            {props.children}
        </RestContext.Provider>
    )
}
