import React from 'react'

const StarRating = ({ rating }) => {
  const imgs = [];
  for (let i = 1; i <= 5; i++) {
    if (i <= rating) {
      imgs.push(<FontAwesomeIcon icon="fa-sharp fa-solid fa-star" />);
    } else if (i === Math.ceil(rating) && !Number.isInteger(rating)) {
      imgs.push(<FontAwesomeIcon icon="fa-sharp fa-solid fa-star" />);
    } else {
      imgs.push(<FontAwesomeIcon icon="fa-sharp fa-solid fa-star" />);
    }
  }
  return <>{imgs}</>;
};

export default StarRating
